    /**
     * 根据当前ID获取省市地区
     *
     * @access public
     * @param string $ip
     * @return array
     */
    public function location($ip) {
        $url = sprintf($this->_config['location_api']['url'], $ip);
        $curl = curl_init();

        curl_setopt($curl, CURLOPT_URL, $url);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($curl, CURLOPT_CONNECTTIMEOUT, 30);

        $result = curl_exec($curl);
        
        curl_close($curl);
        $result = json_decode($result);
        
        if ('-2' == $result) {
            return NULL;
        }
        
        if (!isset($result->ret) || '-1' == $result->ret) {
            return NULL;
        }
        
        return $result->city;
    }

