    /**
     * 获取城市对应的code
     *
     * @access private
     * @param string $city 城市名称
     * @return string
     */
    private function _getCityCode($city) {
        $cityData = json_decode($this->_cityCode(), TRUE);
        
        return $cityData[$city];
    }


    /**
     * 发送请求获取天气
     *
     * @author mjf<1051020685@qq.com>
     * @copyright 2017-2-22
     * @param unknown $weatherKey
     * @param unknown $value
     * @return mixed
     */
    private function _sendCurl($weatherKey, $value){
        $curl = curl_init();
        $url = sprintf($this->_config[$weatherKey]['url'], $value);
        
        if (isset($this->_config[$weatherKey]['header'])) {
            curl_setopt($curl, CURLOPT_HTTPHEADER, $this->_config[$weatherKey]['header']);
        }
    
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);
    
        curl_setopt($curl, CURLOPT_URL, $url);
        $result = json_decode(curl_exec($curl), TRUE);
    
        return $result;
    }
    
    /**
     * 根据IP或者城市代码获取天气，百度api接口
     *
     * @access public
     * @param string $ip
     * @return weather
     */
    public function weatherBaidu($ip, $type='ip') {
        $buildQuery = 'ip='.$ip;
        
        if ('city' == $type) {
            $city = $this->location($ip);
            
            if (!empty($city)) {
                $this->city = $city;
            }
            
            $cityCode = $this->_getCityCode($this->city);
            $buildQuery = 'citycode=' . $cityCode;
        }
        
        $this->_config['baidu_api']['url'] .= $buildQuery;
        
        $result = $this->_sendCurl('baidu_api', '');
        
        if (empty($result['result'])) {
            return [];
        }
  
        return $result['result'];
    }
    
    /**
     * 根据ip获取天气,和世界接口
     * 
     * @author mjf<1051020685@qq.com>
     * @copyright 2017-2-22
     * @param unknown $ip
     * @return multitype:string Ambigous <string, unknown>
     */
    public function weatherHe($ip){
        $city = $this->location($ip);
        
        if (!empty($city)) {
            $this->city = $city;
        }
        
        $pinyinArray = $this->_citypinyin();
        $pinyin = !empty($pinyinArray[$this->city]) ? $pinyinArray[$this->city] : 'shanghai';
        
        $result = $this->_sendCurl('heshijie_weather_api', $pinyin);
        
        $now = !empty($result['HeWeather data service 3.0'][0]['now']) ? $result['HeWeather data service 3.0'][0]['now'] : [];
        
        $betweenTemp = !empty($result['HeWeather data service 3.0'][0]['daily_forecast'][0]['tmp']) ? $result['HeWeather data service 3.0'][0]['daily_forecast'][0]['tmp'] : [];
        
        return [
            'city' => $this->city, 
            'weather' => isset($now['cond']['txt']) ? $now['cond']['txt'] : '', 
            'tempeather' => isset($now['tmp']) ? $now['tmp'] : '', 
            'lTempeather' => isset($betweenTemp['min']) ? $betweenTemp['min'] : '', 
            'hTempeather' => isset($betweenTemp['max']) ? $betweenTemp['max'] : '',
        ];
    }
    
    /**
     * 阿里云获取接口
     * 
     * @author mjf<1051020685@qq.com>
     * @copyright 2017-2-27
     * @param unknown $ip
     */
    public function weatherAliyun($ip){
        $city = $this->location($ip);
        
        if (!empty($city)) {
            $this->city = $city;
        }
        
        
        //域名后、query前的部分
        $path = "/area-to-weather";
        
        $aliyunConfig = $this->_config['aliyun_api'];
       
        require COMMON_PATH . DIRECTORY_SEPARATOR . 'Tool' . DIRECTORY_SEPARATOR
        . 'weather' . DIRECTORY_SEPARATOR. 'Util' . DIRECTORY_SEPARATOR . 'Autoloader.php';
        
        $request = new \HttpRequest($aliyunConfig['host'], $path, \HttpMethod::GET, $aliyunConfig['appKey'], $aliyunConfig['appSecret']);
        
        //设定Content-Type，根据服务器端接受的值来设置
        $request->setHeader(\HttpHeader::HTTP_HEADER_CONTENT_TYPE, \ContentType::CONTENT_TYPE_TEXT);
        
        //设定Accept，根据服务器端接受的值来设置
        $request->setHeader(\HttpHeader::HTTP_HEADER_ACCEPT, \ContentType::CONTENT_TYPE_TEXT);
        
        //注意：业务query部分，如果没有则无此行；请不要、不要、不要做UrlEncode处理
        
        $request->setQuery("area", $this->city);
        
        $response = \HttpClient::execute($request);
        
        $result = json_decode($response->getBody(), true);
        
        $result = !empty($result['showapi_res_body']) ? $result['showapi_res_body'] : [];
        
        $now = !empty($result['f1']) ? $result['f1'] : [];
        if(empty($now)){
            return [];
        }
        
        return [
            'city' => $this->city, 
            'weather' => isset($result['now']['weather']) ? $result['now']['weather'] : '', 
            'tempeather' => isset($result['now']['temperature']) ? $result['now']['temperature'] : '', 
            'lTempeather' => isset($now['night_air_temperature']) ? $now['night_air_temperature'] : '', 
            'hTempeather' => isset($now['day_air_temperature']) ? $now['day_air_temperature'] : '',
        ];
    }
    
    /**
     * 返回天气数据
     * 
     * @author mjf<1051020685@qq.com>
     * @copyright 2017-2-22
     * @param string $ip
     * @param number $apiType
     */
    public function weather(){
        $ip = get_client_ip();
        $data = $this->weatherBaidu($ip);
        if(!empty($data)){
        	return $data;
        }
        
    	return $this->weatherAliyun($ip);
    }
    
}